/**
 * Add prop "wide" to react component
 */
interface WideProp {
  wide?: boolean;
}

/**
 * Used in select component to set if options list need to be bigger to fit content
 */
interface WideOptionsProp {
  wideOptions?: boolean;
}

/**
 * Used in select component to check if select is hovered
 */
interface IsHoveredProp {
  isHovered?: boolean;
}
